// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract EnergyManagement {

    address public energyHouse;
    mapping(address => uint256) public energyBalance;
    
    // Event to log energy allocations
    event EnergyAllocated(address indexed to, uint256 amount);
    event EnergyTraded(address indexed from, address indexed to, uint256 amount, string paymentMethod);

    constructor() {
        energyHouse = msg.sender;  // The contract deployer is the energy house initially
    }

    // Function to allocate energy to a house
    function allocateEnergy(address house, uint256 amount) external {
        require(msg.sender == energyHouse, "Only the energy house can allocate energy.");
        energyBalance[house] += amount;
        emit EnergyAllocated(house, amount);
    }

    // Function to trade energy between houses
    function tradeEnergy(address to, uint256 amount, string memory paymentMethod) external {
        require(energyBalance[msg.sender] >= amount, "Insufficient energy balance.");
        energyBalance[msg.sender] -= amount;
        energyBalance[to] += amount;
        emit EnergyTraded(msg.sender, to, amount, paymentMethod);
    }

    // Function to check the energy balance of an address
    function getEnergyBalance(address house) external view returns (uint256) {
        return energyBalance[house];
    }
}
